import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { FiLogOut, FiUpload, FiEye, FiHome, FiPlus, FiSave } from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";
import styled from "styled-components";

const SidebarContainer = styled.div`
  height: 100vh;
  width: ${({ $expanded }) => ($expanded ? "250px" : "80px")};
  background: linear-gradient(180deg, #0f0c29 0%, #302b63 50%, #24243e 100%);
  color: white;
  display: flex;
  flex-direction: column;
  position: fixed;
  left: 0;
  top: 0;
  z-index: 100;
  transition: width 0.3s ease;
  overflow: hidden;
`;

const MenuItem = styled.div`
  display: flex;
  align-items: center;
  padding: 15px 20px;
  margin: 5px 10px;
  border-radius: 8px;
  cursor: pointer;
  background: ${({ $active }) => ($active ? "rgba(124, 58, 237, 0.2)" : "transparent")};
  color: ${({ $active }) => ($active ? "#fff" : "rgba(255, 255, 255, 0.7)")};
  transition: all 0.3s ease;

  &:hover {
    background: rgba(124, 58, 237, 0.3);
  }
`;

const MenuIcon = styled.div`
  font-size: 20px;
  margin-right: 15px;
`;

const MenuText = styled.span`
  font-size: 16px;
  white-space: nowrap;
`;

export default function Sidebar({ activeTab, setActiveTab }) {
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [expanded, setExpanded] = useState(true);

  const menuItems = [
    { icon: <FiHome />, label: "Главная", action: () => navigate("/") },
    { icon: <FiUpload />, label: "Загрузка", tab: "upload" },
    { icon: <FiPlus />, label: "Создать", tab: "create" },
    { icon: <FiSave />, label: "Сохраненные", tab: "saved" },
    { icon: <FiEye />, label: "Квизы", action: () => navigate("/quizzes") },
  ];

  const handleItemClick = (item) => {
    if (item.action) {
      item.action();
    } else if (item.tab) {
      setActiveTab(item.tab);
    }
  };

  return (
    <SidebarContainer $expanded={expanded}>
      <div style={{ padding: "20px" }}>
        {menuItems.map((item) => (
          <MenuItem
            key={item.label}
            $active={activeTab === item.tab}
            onClick={() => handleItemClick(item)}
          >
            <MenuIcon>{item.icon}</MenuIcon>
            {expanded && <MenuText>{item.label}</MenuText>}
          </MenuItem>
        ))}
      </div>

      <div style={{ marginTop: "auto", padding: "20px" }}>
        <MenuItem onClick={logout}>
          <MenuIcon><FiLogOut /></MenuIcon>
          {expanded && <MenuText>Выйти</MenuText>}
        </MenuItem>
      </div>
    </SidebarContainer>
  );
}
