            style={{
              display: "flex",
              alignItems: "center",
              padding: "10px",
              borderRadius: "8px",
              cursor: "pointer",
              marginBottom: "10px",
              color: "#fff",
            }}
            onClick={() => setActiveTab('create')}
          >
            <div style={{ fontSize: "20px", marginRight: "12px", minWidth: "24px" }}>
              <FiPlus />
            </div>
            <span style={{ whiteSpace: "nowrap", opacity: sidebarWidth > 60 ? 1 : 0, transition: "opacity 0.3s" }}>
              Create New
            </span>
          </div>
          <div 
            style={{
              display: "flex",
              alignItems: "center",
              padding: "10px",
              borderRadius: "8px",
              cursor: "pointer",
              marginBottom: "10px",
              color: "#fff",
            }}
            onClick={() => setActiveTab('saved')}
          >
            <div style={{ fontSize: "20px", marginRight: "12px", minWidth: "24px" }}>
              <FiSave />
            </div>
            <span style={{ whiteSpace: "nowrap", opacity: sidebarWidth > 60 ? 1 : 0, transition: "opacity 0.3s" }}>
              Saved Quizzes
            </span>
          </div>
