import React, { useState, useEffect } from "react";
import yaml from "js-yaml";
import { FiLogOut, FiUpload, FiEye, FiX, FiCheck, FiPlus, FiTrash2, FiSave } from "react-icons/fi";
import { useAuth } from "../context/AuthContext";
import "../styles/ConfigUploadPage.css"

const YamlExample = () => {
  return (
    <div className="yaml-example">
      <div>
        <div className="yaml-key">quiz:</div>
        <div style={{ marginLeft: "20px" }} className="yaml-key">title: <span className="yaml-string">"Пример квиза"</span></div>
        <div style={{ marginLeft: "20px" }} className="yaml-key">description: <span className="yaml-string">"Тест на общие знания"</span></div>
        <div style={{ marginLeft: "20px" }} className="yaml-key">questions:</div>

        <div style={{ marginLeft: "40px" }} className="yaml-key">- id: <span className="yaml-number">1</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">question: <span className="yaml-string">"Какой язык программирования используется для фронтенда?"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">type: <span className="yaml-string">"single"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">options:</div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"Python"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"JavaScript"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"C++"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"Ruby"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">correct_option_index: <span className="yaml-number">1</span></div>

        <div style={{ marginLeft: "40px" }} className="yaml-key">- id: <span className="yaml-number">2</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">question: <span className="yaml-string">"Выберите все правильные ответы!"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">type: <span className="yaml-string">"multiple"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">options:</div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"HTML"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"CSS"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"SQL"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"JavaScript"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">correct_option_indexes: <span className="yaml-number">[0, 1, 3]</span></div>

        <div style={{ marginLeft: "40px" }} className="yaml-key">- id: <span className="yaml-number">3</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">question: <span className="yaml-string">"Что означает CSS?"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">type: <span className="yaml-string">"single"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">options:</div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"Cascading Style Sheets"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"Computer Style Sheets"</span></div>
        <div style={{ marginLeft: "80px" }} className="yaml-string">- <span className="yaml-string">"Creative Style System"</span></div>
        <div style={{ marginLeft: "60px" }} className="yaml-key">correct_option_index: <span className="yaml-number">0</span></div>
      </div>
    </div>
  );
};

function Sidebar({ width, setWidth }) {
  const { logout } = useAuth();

  return (
    <div
      className="sidebar"
      style={{ width: `${width}px` }}
      onMouseEnter={() => setWidth(200)}
      onMouseLeave={() => setWidth(60)}
    >
      <div className="sidebar-section">
        <SidebarItem icon={<FiUpload />} label="Upload" />
        <SidebarItem icon={<FiEye />} label="Quizzes" />
      </div>
      <div className="sidebar-section">
        <SidebarItem icon={<FiLogOut />} label="Logout" onClick={logout} />
      </div>
    </div>
  );
}

function SidebarItem({ icon, label, onClick }) {
  return (
    <div className="sidebar-item" onClick={onClick}>
      <div className="sidebar-icon">{icon}</div>
      <span className="sidebar-label">{label}</span>
    </div>
  );
}

function QuizModal({ visible, onClose, quizConfig }) {
  if (!visible) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close" onClick={onClose} aria-label="Close modal">
          <FiX />
        </button>
        <Quiz quizConfig={quizConfig} />
      </div>
    </div>
  );
}

function Quiz({ quizConfig }) {
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const handleChange = (questionId, optionIndex, isMultiple) => {
    setAnswers((prev) => {
      const current = prev[questionId] || (isMultiple ? [] : null);

      if (isMultiple) {
        if (current.includes(optionIndex)) {
          return { ...prev, [questionId]: current.filter((i) => i !== optionIndex) };
        } else {
          return { ...prev, [questionId]: [...current, optionIndex] };
        }
      } else {
        return { ...prev, [questionId]: optionIndex };
      }
    });
  };

  const checkAnswers = () => {
    const questions = quizConfig.quiz.questions;
    let correct = 0;

    questions.forEach((q) => {
      const a = answers[q.id];
      if (q.type === "single" && a === q.correct_option_index) correct++;
      if (
        q.type === "multiple" &&
        Array.isArray(a) &&
        a.length === q.correct_option_indexes.length &&
        a.every((val) => q.correct_option_indexes.includes(val))
      ) {
        correct++;
      }
    });

    setResult(`Правильных ответов: ${correct} из ${questions.length}`);
  };

  return (
    <div>
      <h2 style={{ color: "#010528" }}>{quizConfig.quiz.title}</h2>
      <p>{quizConfig.quiz.description}</p>
      {quizConfig.quiz.questions.map((q) => (
        <div key={q.id} style={{ marginBottom: 20 }}>
          <strong style={{ color: "#010528" }}>{q.id}. {q.question}</strong>
          {q.options.map((opt, i) => {
            const isMultiple = q.type === "multiple";
            const checked = isMultiple
              ? (answers[q.id] || []).includes(i)
              : answers[q.id] === i;

            return (
              <label
                key={i}
                style={{
                  display: "flex",
                  alignItems: "center",
                  marginLeft: 10,
                  cursor: "pointer",
                  userSelect: "none",
                  marginTop: 6,
                }}
              >
                <input
                  type={isMultiple ? "checkbox" : "radio"}
                  name={`q-${q.id}`}
                  checked={checked}
                  onChange={() => handleChange(q.id, i, isMultiple)}
                  style={{ display: "none" }}
                />
                <CustomCheckbox checked={checked} isRadio={!isMultiple} />
                <span style={{ marginLeft: 8, color: "#010528" }}>{opt}</span>
              </label>
            );
          })}
        </div>
      ))}
      <button
        onClick={checkAnswers}
        className="button button-primary"
      >
        Проверить ответы
      </button>
      {result && (
        <p style={{ fontWeight: "bold", marginTop: 10, color: "#010528" }}>
          {result}
        </p>
      )}
    </div>
  );
}

function CustomCheckbox({ checked, isRadio }) {
  return (
    <span className={isRadio ? `custom-radio ${checked ? 'checked' : ''}` : `custom-checkbox ${checked ? 'checked' : ''}`}>
      {checked && !isRadio && <FiCheck color="#fff" size={16} />}
    </span>
  );
}

function ConfigUploadPage() {
  const [quizConfig, setQuizConfig] = useState(null);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sidebarWidth, setSidebarWidth] = useState(60);
  const [activeTab, setActiveTab] = useState('upload');
  const [savedQuizzes, setSavedQuizzes] = useState([]);

  const [quizTitle, setQuizTitle] = useState("");
  const [quizDescription, setQuizDescription] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    id: 1,
    text: "",
    type: "single",
    options: ["", ""],
    correctOption: null,
    correctOptions: []
  });

  useEffect(() => {
    const saved = localStorage.getItem('savedQuizzes');
    if (saved) {
      setSavedQuizzes(JSON.parse(saved));
    }
  }, []);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const parsed = yaml.load(e.target.result);
        setQuizConfig(parsed);
        setError(null);
        setShowModal(true);
      } catch (err) {
        setError("Ошибка парсинга YAML: " + err.message);
        setQuizConfig(null);
      }
    };

    reader.readAsText(file);
  };

  const saveQuiz = () => {
    const newQuiz = {
      id: Date.now(),
      title: quizTitle,
      description: quizDescription,
      questions: questions,
      createdAt: new Date().toISOString()
    };

    const updatedQuizzes = [...savedQuizzes, newQuiz];
    setSavedQuizzes(updatedQuizzes);
    localStorage.setItem('savedQuizzes', JSON.stringify(updatedQuizzes));
    alert('Квиз успешно сохранен!');
  };

  const loadSavedQuiz = (quiz) => {
    setQuizTitle(quiz.title);
    setQuizDescription(quiz.description);
    setQuestions(quiz.questions);
    setActiveTab('create');
  };

  const deleteSavedQuiz = (id, e) => {
    e.stopPropagation();
    const updatedQuizzes = savedQuizzes.filter(q => q.id !== id);
    setSavedQuizzes(updatedQuizzes);
    localStorage.setItem('savedQuizzes', JSON.stringify(updatedQuizzes));
  };

  const addQuestion = () => {
    const newQuestion = {
      id: questions.length + 1,
      question: currentQuestion.text,
      type: currentQuestion.type,
      options: currentQuestion.options.filter(opt => opt.trim() !== ""),
      ...(currentQuestion.type === "single" 
        ? { correct_option_index: currentQuestion.correctOption } 
        : { correct_option_indexes: currentQuestion.correctOptions })
    };

    setQuestions([...questions, newQuestion]);
    setCurrentQuestion({
      id: questions.length + 2,
      text: "",
      type: "single",
      options: ["", ""],
      correctOption: null,
      correctOptions: []
    });
  };

  const updateOption = (index, value) => {
    const newOptions = [...currentQuestion.options];
    newOptions[index] = value;
    setCurrentQuestion({...currentQuestion, options: newOptions});
  };

  const addOption = () => {
    setCurrentQuestion({
      ...currentQuestion,
      options: [...currentQuestion.options, ""]
    });
  };

  const removeOption = (index) => {
    const newOptions = currentQuestion.options.filter((_, i) => i !== index);
    setCurrentQuestion({
      ...currentQuestion,
      options: newOptions,
      correctOption: currentQuestion.correctOption === index ? null : 
                   currentQuestion.correctOption > index ? currentQuestion.correctOption - 1 : 
                   currentQuestion.correctOption,
      correctOptions: currentQuestion.correctOptions
        .map(opt => opt > index ? opt - 1 : opt)
        .filter(opt => opt !== index)
    });
  };

  const handleCorrectAnswerChange = (index, isChecked) => {
    if (currentQuestion.type === "single") {
      setCurrentQuestion({...currentQuestion, correctOption: index});
    } else {
      const newCorrect = isChecked 
        ? [...currentQuestion.correctOptions, index]
        : currentQuestion.correctOptions.filter(i => i !== index);
      setCurrentQuestion({...currentQuestion, correctOptions: newCorrect});
    }
  };

  const generateYAML = () => {
    const quiz = {
      quiz: {
        title: quizTitle,
        description: quizDescription,
        questions: questions
      }
    };

    const yamlStr = yaml.dump(quiz);
    const blob = new Blob([yamlStr], { type: "text/yaml" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = `quiz_${Date.now()}.yaml`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const previewQuiz = () => {
    setQuizConfig({
      quiz: {
        title: quizTitle,
        description: quizDescription,
        questions: questions
      }
    });
    setShowModal(true);
  };

  const renderGUICreator = () => (
    <div className="card">
      <h2 style={{ color: "#010528", marginBottom: "20px" }}>Create new quiz</h2>

      <div className="form-group">
        <label className="form-label">Quiz Title:</label>
        <input
          type="text"
          className="form-input"
          value={quizTitle}
          onChange={(e) => setQuizTitle(e.target.value)}
          placeholder="Enter quiz title"
        />
      </div>

      <div className="form-group">
        <label className="form-label">Description:</label>
        <textarea
          className="form-textarea"
          value={quizDescription}
          onChange={(e) => setQuizDescription(e.target.value)}
          placeholder="Enter quiz description"
        />
      </div>

      <div style={{ 
        backgroundColor: "#f8f9fa",
        padding: "20px",
        borderRadius: "8px",
        marginBottom: "25px"
      }}>
        <h3 style={{ color: "#010528", marginTop: 0, marginBottom: "15px" }}>Add Question</h3>

        <div className="form-group">
          <label className="form-label">Question Text:</label>
          <input
            type="text"
            className="form-input"
            value={currentQuestion.text}
            onChange={(e) => setCurrentQuestion({...currentQuestion, text: e.target.value})}
            placeholder="Enter your question"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Question Type:</label>
          <select
            className="form-select"
            value={currentQuestion.type}
            onChange={(e) => setCurrentQuestion({
              ...currentQuestion, 
              type: e.target.value,
              correctOption: null,
              correctOptions: []
            })}
          >
            <option value="single">Single correct answer</option>
            <option value="multiple">Multiple correct answers</option>
          </select>
        </div>

        <div style={{ marginBottom: "20px" }}>
          <label className="form-label">Options:</label>
          {currentQuestion.options.map((option, index) => (
            <div key={index} className="option-item">
              <input
                type={currentQuestion.type === "single" ? "radio" : "checkbox"}
                name={`correct-answer-${currentQuestion.id}`}
                checked={
                  currentQuestion.type === "single" 
                    ? currentQuestion.correctOption === index 
                    : currentQuestion.correctOptions.includes(index)
                }
                onChange={(e) => handleCorrectAnswerChange(index, e.target.checked)}
                style={{ transform: "scale(1.2)" }}
              />
              <input
                type="text"
                className="option-input"
                value={option}
                onChange={(e) => updateOption(index, e.target.value)}
                placeholder={`Option ${index + 1}`}
              />
              <button 
                className="remove-option"
                onClick={() => removeOption(index)}
              >
                <FiTrash2 />
              </button>
            </div>
          ))}
          <button
            onClick={addOption}
            className="button"
            style={{
              backgroundColor: "#f0f0f0",
              color: "#010528",
              padding: "8px 15px",
              marginTop: "10px"
            }}
          >
            <FiPlus size={16} /> Add Option
          </button>
        </div>

        <button
          onClick={addQuestion}
          disabled={!currentQuestion.text || 
                   currentQuestion.options.every(opt => !opt.trim()) ||
                   (currentQuestion.type === "single" && currentQuestion.correctOption === null) ||
                   (currentQuestion.type === "multiple" && currentQuestion.correctOptions.length === 0)}
          className="button button-primary"
        >
          Add Question
        </button>
      </div>

      {questions.length > 0 && (
        <div>
          <h3 style={{ color: "#010528", marginBottom: "15px" }}>Added Questions ({questions.length})</h3>
          <div style={{ 
            backgroundColor: "#f8f9fa", 
            padding: "15px", 
            borderRadius: "8px",
            marginBottom: "25px"
          }}>
            {questions.map((q, i) => (
              <div key={i} style={{ 
                padding: "12px", 
                borderBottom: i < questions.length - 1 ? "1px solid #ddd" : "none",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center"
              }}>
                <div>
                  <div style={{ fontWeight: "500", marginBottom: "5px" }}>{q.id}. {q.question}</div>
                  <div style={{ fontSize: "14px", color: "#666" }}>
                    Type: {q.type === "single" ? "Single answer" : "Multiple answers"}
                  </div>
                </div>
                <button 
                  className="remove-option"
                  onClick={() => setQuestions(questions.filter((_, idx) => idx !== i))}
                >
                  <FiTrash2 />
                </button>
              </div>
            ))}
          </div>

          <div style={{ display: "flex", gap: "15px", flexWrap: 'wrap' }}>
            <button
              onClick={previewQuiz}
              className="button button-blue"
            >
              <FiEye /> Preview Quiz
            </button>
            <button
              onClick={generateYAML}
              disabled={!quizTitle || questions.length === 0}
              className="button button-success"
            >
              <FiUpload /> Export as YAML
            </button>
            <button
              onClick={saveQuiz}
              disabled={!quizTitle || questions.length === 0}
              className="button button-purple"
            >
              <FiSave /> Save Quiz
            </button>
          </div>
        </div>
      )}
    </div>
  );

  const renderSavedQuizzes = () => (
    <div className="card">
      <h2 style={{ color: "#010528", marginBottom: "20px" }}>Saved Quizzes</h2>

      {savedQuizzes.length === 0 ? (
        <p style={{ color: "#666", textAlign: "center" }}>No saved quizzes yet</p>
      ) : (
        <div className="quiz-list">
          {savedQuizzes.map((quiz) => (
            <div 
              key={quiz.id}
              className="quiz-item"
              onClick={() => loadSavedQuiz(quiz)}
            >
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                  <h3 className="quiz-title">{quiz.title}</h3>
                  <p className="quiz-description">{quiz.description}</p>
                  <p className="quiz-date">
                    {new Date(quiz.createdAt).toLocaleString()}
                  </p>
                </div>
                <button 
                  className="delete-quiz"
                  onClick={(e) => deleteSavedQuiz(quiz.id, e)}
                >
                  <FiTrash2 />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  const renderUploadTab = () => (
    <div className="card">
      <h2 style={{ color: "#010528", marginBottom: "20px" }}>Upload Quiz Config</h2>
      <label
        htmlFor="config-upload"
        className="button button-primary"
        style={{ marginBottom: "20px" }}
      >
        <FiUpload style={{ marginRight: "8px" }} /> Select YAML File
        <input
          type="file"
          id="config-upload"
          style={{ display: "none" }}
          accept=".yaml,.yml"
          onChange={handleFileUpload}
        />
      </label>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div style={{ marginTop: "30px" }}>
        <h3 style={{ color: "#010528", marginBottom: "15px" }}>Example YAML Structure</h3>
        <YamlExample />
      </div>
    </div>
  );

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <Sidebar width={sidebarWidth} setWidth={setSidebarWidth} />
      <div className="main-content" style={{ marginLeft: sidebarWidth }}>
        <div style={{ 
          display: "flex", 
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "30px"
        }}>
          <h1 className="page-header">Quiz Creator</h1>
        </div>

        <div className="tabs-container">
          <button
            onClick={() => setActiveTab('upload')}
            className={`tab-button ${activeTab === 'upload' ? 'active' : ''}`}
          >
            <FiUpload /> Upload Config
          </button>
          <button
            onClick={() => setActiveTab('create')}
            className={`tab-button ${activeTab === 'create' ? 'active' : ''}`}
          >
            <FiPlus /> Create New
          </button>
          <button
            onClick={() => setActiveTab('saved')}
            className={`tab-button ${activeTab === 'saved' ? 'active' : ''}`}
          >
            <FiSave /> Saved Quizzes
          </button>
        </div>

        {activeTab === 'upload' ? renderUploadTab() : 
         activeTab === 'create' ? renderGUICreator() : 
         renderSavedQuizzes()}

        <QuizModal
          visible={showModal}
          onClose={() => setShowModal(false)}
          quizConfig={quizConfig}
        />
      </div>
    </div>
  );
}

export default ConfigUploadPage;
