import React, { useState } from "react";
import yaml from "js-yaml";
import { FiLogOut, FiUpload, FiEye, FiX, FiCheck } from "react-icons/fi";
import { useAuth } from "../context/AuthContext";

function Sidebar({ width, setWidth }) {
  const { logout } = useAuth();

  const sidebarStyle = {
    height: "100vh",
    width: `${width}px`,
    backgroundColor: "rgba(1, 5, 40, 0.8)", // #010528 с прозрачностью
    color: "#fff",
    transition: "width 0.3s ease",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    position: "fixed",
    left: 0,
    top: 0,
    zIndex: 10,
    boxShadow: "4px 0 8px rgba(0,0,0,0.1)",
    overflow: "hidden",
  };

  const sectionStyle = {
    padding: "20px 10px",
  };

  return (
    <div
      style={sidebarStyle}
      onMouseEnter={() => setWidth(200)}
      onMouseLeave={() => setWidth(60)}
    >
      <div style={sectionStyle}>
        <SidebarItem icon={<FiUpload />} label="Upload" />
        <SidebarItem icon={<FiEye />} label="Quizzes" />
      </div>
      <div style={sectionStyle}>
        <SidebarItem icon={<FiLogOut />} label="Logout" onClick={logout} />
      </div>
    </div>
  );
}

function SidebarItem({ icon, label, onClick }) {
  const itemStyle = {
    display: "flex",
    alignItems: "center",
    padding: "10px 10px",
    borderRadius: "8px",
    cursor: "pointer",
    marginBottom: "10px",
    transition: "background 0.2s ease",
    color: "#fff",
  };

  const iconStyle = {
    fontSize: "20px",
    marginRight: "12px",
    minWidth: "24px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "#fff",
  };

  const labelStyle = {
    whiteSpace: "nowrap",
    overflow: "hidden",
    opacity: 0,
    transition: "opacity 0.3s ease",
  };

  const handleMouseEnter = (e) => {
    e.currentTarget.style.background = "#010528";
    const labelSpan = e.currentTarget.querySelector("span");
    if (labelSpan) labelSpan.style.opacity = 1;
  };

  const handleMouseLeave = (e) => {
    e.currentTarget.style.background = "transparent";
    const labelSpan = e.currentTarget.querySelector("span");
    if (labelSpan) labelSpan.style.opacity = 0;
  };

  return (
    <div
      style={itemStyle}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={onClick}
    >
      <div style={iconStyle}>{icon}</div>
      <span style={labelStyle}>{label}</span>
    </div>
  );
}

function QuizCard({ quiz, onClick }) {
  return (
    <div
      onClick={onClick}
      style={{
        cursor: "pointer",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "12px",
        backgroundColor: "#f0f0f0",
        width: "300px",
        marginTop: "20px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        position: "relative",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <h3 style={{ marginTop: 0 }}>{quiz.title}</h3>
      <p>{quiz.description}</p>
    </div>
  );
}

function QuizModal({ visible, onClose, quizConfig }) {
  if (!visible) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0, left: 0, right: 0, bottom: 0,
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 999,
      }}
    >
      <div
        style={{
          backgroundColor: "#fff",
          padding: "30px",
          borderRadius: "12px",
          width: "90%",
          maxWidth: "700px",
          maxHeight: "90vh",
          overflowY: "auto",
          position: "relative",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        }}
      >
        <button
          onClick={onClose}
          aria-label="Close modal"
          style={{
            position: "absolute",
            top: "15px",
            right: "15px",
            fontWeight: "bold",
            fontSize: "24px",
            border: "none",
            background: "none",
            cursor: "pointer",
            color: "#010528",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <FiX />
        </button>
        <Quiz quizConfig={quizConfig} />
      </div>
    </div>
  );
}

function Quiz({ quizConfig }) {
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const handleChange = (questionId, optionIndex, isMultiple) => {
    setAnswers((prev) => {
      const current = prev[questionId] || (isMultiple ? [] : null);

      if (isMultiple) {
        if (current.includes(optionIndex)) {
          return { ...prev, [questionId]: current.filter((i) => i !== optionIndex) };
        } else {
          return { ...prev, [questionId]: [...current, optionIndex] };
        }
      } else {
        return { ...prev, [questionId]: optionIndex };
      }
    });
  };

  const checkAnswers = () => {
    const questions = quizConfig.quiz.questions;
    let correct = 0;

    questions.forEach((q) => {
      const a = answers[q.id];
      if (q.type === "single" && a === q.correct_option_index) correct++;
      if (
        q.type === "multiple" &&
        Array.isArray(a) &&
        a.length === q.correct_option_indexes.length &&
        a.every((val) => q.correct_option_indexes.includes(val))
      ) {
        correct++;
      }
    });

    setResult(`Правильных ответов: ${correct} из ${questions.length}`);
  };

  return (
    <div>
      <h2 style={{ color: "#010528" }}>{quizConfig.quiz.title}</h2>
      <p>{quizConfig.quiz.description}</p>
      {quizConfig.quiz.questions.map((q) => (
        <div key={q.id} style={{ marginBottom: 20 }}>
          <strong style={{ color: "#010528" }}>{q.id}. {q.question}</strong>
          {q.options.map((opt, i) => {
            const isMultiple = q.type === "multiple";
            const checked = isMultiple
              ? (answers[q.id] || []).includes(i)
              : answers[q.id] === i;

            // Кастомный чекбокс/радио
            return (
              <label
                key={i}
                style={{
                  display: "flex",
                  alignItems: "center",
                  marginLeft: 10,
                  cursor: "pointer",
                  userSelect: "none",
                  marginTop: 6,
                }}
              >
                <input
                  type={isMultiple ? "checkbox" : "radio"}
                  name={`q-${q.id}`}
                  checked={checked}
                  onChange={() => handleChange(q.id, i, isMultiple)}
                  style={{ display: "none" }}
                />
                <CustomCheckbox checked={checked} isRadio={!isMultiple} />
                <span style={{ marginLeft: 8, color: "#010528" }}>{opt}</span>
              </label>
            );
          })}
        </div>
      ))}
      <button
        onClick={checkAnswers}
        style={{
          padding: "10px 20px",
          marginTop: 10,
          backgroundColor: "#010528",
          color: "#fff",
          border: "none",
          borderRadius: "6px",
          cursor: "pointer",
          fontWeight: "bold",
          transition: "background-color 0.3s ease",
        }}
        onMouseEnter={e => e.currentTarget.style.backgroundColor = "#0921E6"}
        onMouseLeave={e => e.currentTarget.style.backgroundColor = "#010528"}
      >
        Проверить ответы
      </button>
      {result && (
        <p style={{ fontWeight: "bold", marginTop: 10, color: "#010528" }}>
          {result}
        </p>
      )}
    </div>
  );
}

function CustomCheckbox({ checked, isRadio }) {
  // isRadio = true - радиокнопка, иначе чекбокс
  return (
    <span
      style={{
        width: 20,
        height: 20,
        borderRadius: isRadio ? "50%" : "4px",
        border: "2px solid #010528",
        backgroundColor: checked ? "#010528" : "transparent",
        display: "inline-flex",
        alignItems: "center",
        justifyContent: "center",
        transition: "background-color 0.3s ease, border-color 0.3s ease",
        boxSizing: "border-box",
        position: "relative",
      }}
    >
      {checked && (
        isRadio ? (
          <span
            style={{
              width: 10,
              height: 10,
              borderRadius: "50%",
              backgroundColor: "#fff",
              display: "block",
            }}
          />
        ) : (
          <FiCheck color="#fff" size={16} />
        )
      )}
    </span>
  );
}

function ConfigUploadPage() {
  const [quizConfig, setQuizConfig] = useState(null);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sidebarWidth, setSidebarWidth] = useState(60);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const parsed = yaml.load(e.target.result);
        setQuizConfig(parsed);
        setError(null);
      } catch (err) {
        setError("Ошибка парсинга YAML: " + err.message);
        setQuizConfig(null);
      }
    };

    reader.readAsText(file);
  };

  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#fff" }}>
      <Sidebar width={sidebarWidth} setWidth={setSidebarWidth} />
      <div
        style={{
          padding: "40px",
          flexGrow: 1,
          marginLeft: sidebarWidth,
          transition: "margin-left 0.3s ease",
          backgroundColor: "#fff",
          overflowY: "auto",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        }}
      >
        <h1 style={{ color: "#010528" }}>New Quiz</h1>
        <label
          htmlFor="config-upload"
          style={{
            display: "inline-block",
            padding: "12px 24px",
            backgroundColor: "#d3d3d3",
            borderRadius: "10px",
            cursor: "pointer",
            fontWeight: "bold",
            color: "#010528",
            userSelect: "none",
          }}
        >
          Upload config file
        </label>
        <input
          type="file"
          id="config-upload"
          style={{ display: "none" }}
          accept=".yaml,.yml"
          onChange={handleFileUpload}
        />
        {error && <p style={{ color: "red", marginTop: 20 }}>{error}</p>}
        {quizConfig && (
          <QuizCard quiz={quizConfig.quiz} onClick={() => setShowModal(true)} />
        )}
        <QuizModal
          visible={showModal}
          onClose={() => setShowModal(false)}
          quizConfig={quizConfig}
        />
      </div>
    </div>
  );
}

export default ConfigUploadPage;
