import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { 
  FiLogOut, 
  FiUpload, 
  FiEye, 
  FiHome, 
  FiPlus, 
  FiSave,
  FiSettings
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";
import styled from "styled-components";

const SidebarContainer = styled(motion.div)`
  height: 100vh;
  width: ${({ $expanded }) => ($expanded ? "280px" : "88px")};
  background: linear-gradient(180deg, #0f0c29 0%, #302b63 50%, #24243e 100%);
  color: white;
  display: flex;
  flex-direction: column;
  position: fixed;
  left: 0;
  top: 0;
  z-index: 100;
  box-shadow: 10px 0 30px rgba(0, 0, 0, 0.3);
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.1);
  overflow: hidden;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 28px 24px;
  margin-bottom: 20px;
  cursor: pointer;
`;

const LogoIcon = styled(motion.div)`
  width: 44px;
  height: 44px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 16px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
`;

const LogoText = styled(motion.h3)`
  margin: 0;
  font-size: 20px;
  font-weight: 700;
  background: linear-gradient(to right, #fff 20%, #a5b4fc 80%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  white-space: nowrap;
`;

const MenuSection = styled.div`
  padding: 0 16px;
  margin-bottom: 24px;
`;

const MenuItem = styled(motion.div)`
  display: flex;
  align-items: center;
  padding: 14px 20px;
  margin-bottom: 8px;
  border-radius: 12px;
  cursor: pointer;
  position: relative;
  overflow: hidden;
  background: ${({ $active }) => 
    $active ? "rgba(124, 58, 237, 0.15)" : "transparent"};
  color: ${({ $active }) => ($active ? "#fff" : "rgba(255, 255, 255, 0.7)")};
  transition: all 0.3s ease;

  &:hover {
    background: ${({ $active }) => 
      $active ? "rgba(124, 58, 237, 0.25)" : "rgba(255, 255, 255, 0.05)"};
    color: white;
  }
`;

const MenuIcon = styled.div`
  font-size: 20px;
  min-width: 24px;
  margin-right: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const MenuText = styled(motion.span)`
  font-size: 15px;
  font-weight: 500;
  white-space: nowrap;
`;

const ActiveIndicator = styled(motion.div)`
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 4px;
  background: linear-gradient(to bottom, #8a2be2, #667eea);
  border-radius: 0 4px 4px 0;
`;

const Badge = styled(motion.span)`
  background: rgba(255, 255, 255, 0.1);
  color: white;
  border-radius: 12px;
  padding: 2px 8px;
  font-size: 12px;
  margin-left: auto;
`;

function Sidebar({ activeTab, setActiveTab, savedQuizzesCount }) {
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [expanded, setExpanded] = useState(false);

  const menuItems = [
    { icon: <FiHome />, label: "Главная", path: "/", name: "home" },
    { 
      icon: <FiUpload />, 
      label: "Загрузка", 
      tab: "upload", 
      name: "upload",
      active: activeTab === 'upload'
    },
    { 
      icon: <FiPlus />, 
      label: "Создать", 
      tab: "create", 
      name: "create",
      active: activeTab === 'create'
    },
    { 
      icon: <FiSave />, 
      label: "Сохраненные", 
      tab: "saved", 
      name: "saved",
      active: activeTab === 'saved',
      badge: savedQuizzesCount > 0 ? savedQuizzesCount : null
    },
    { 
      icon: <FiEye />, 
      label: "Квизы", 
      path: "/quizzes", 
      name: "quizzes",
      active: false
    },
    { 
      icon: <FiSettings />, 
      label: "Настройки", 
      path: "/settings", 
      name: "settings",
      active: false
    },
  ];

  const handleNavigation = (item) => {
    if (item.path) {
      navigate(item.path);
    } else if (item.tab) {
      setActiveTab(item.tab);
    }
  };

  return (
    <SidebarContainer
      $expanded={expanded}
      onMouseEnter={() => setExpanded(true)}
      onMouseLeave={() => setExpanded(false)}
      initial={false}
    >
      <LogoContainer onClick={() => navigate("/")}>
        <LogoIcon
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <FiHome size={24} style={{ color: "white" }} />
        </LogoIcon>
        <AnimatePresence>
          {expanded && (
            <LogoText
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -10 }}
              transition={{ duration: 0.2 }}
            >
              QuizMaster
            </LogoText>
          )}
        </AnimatePresence>
      </LogoContainer>

      <MenuSection>
        {menuItems.map((item) => (
          <MenuItem
            key={item.name}
            $active={item.active}
            onClick={() => handleNavigation(item)}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            {item.active && (
              <ActiveIndicator layoutId="activeIndicator" />
            )}
            <MenuIcon>{item.icon}</MenuIcon>
            <AnimatePresence>
              {expanded && (
                <>
                  <MenuText
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -10 }}
                    transition={{ duration: 0.2 }}
                  >
                    {item.label}
                  </MenuText>
                  {item.badge && (
                    <Badge
                      initial={{ opacity: 0, scale: 0.5 }}
                      animate={{ opacity: 1, scale: 1 }}
                      exit={{ opacity: 0, scale: 0.5 }}
                    >
                      {item.badge}
                    </Badge>
                  )}
                </>
              )}
            </AnimatePresence>
          </MenuItem>
        ))}
      </MenuSection>

      <MenuSection style={{ marginTop: "auto", marginBottom: "32px" }}>
        <MenuItem
          onClick={logout}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          style={{ color: "rgba(255, 107, 107, 0.8)" }}
        >
          <MenuIcon>
            <FiLogOut />
          </MenuIcon>
          <AnimatePresence>
            {expanded && (
              <MenuText
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -10 }}
                transition={{ duration: 0.2 }}
              >
                Выйти
              </MenuText>
            )}
          </AnimatePresence>
        </MenuItem>
      </MenuSection>
    </SidebarContainer>
  );
}

export default Sidebar;
