function QuizzesPage() {
  const [quizzes, setQuizzes] = useState([
    {
      quiz: {
        title: "Sample Quiz 1",
        description: "Test your basic knowledge",
        questions: [
          {
            id: 1,
            question: "What is 2+2?",
            type: "single",
            options: ["3", "4", "5", "6"],
            correct_option_index: 1
          }
        ]
      }
    },
    {
      quiz: {
        title: "Programming Quiz",
        description: "Test your programming knowledge",
        questions: [
          {
            id: 1,
            question: "Which language runs in a web browser?",
            type: "single",
            options: ["Java", "C", "Python", "JavaScript"],
            correct_option_index: 3
          },
          {
            id: 2,
            question: "What are the basic data types in Python?",
            type: "multiple",
            options: ["int", "float", "string", "boolean"],
            correct_option_indexes: [0, 1, 2, 3]
          }
        ]
      }
    }
  ]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sidebarWidth, setSidebarWidth] = useState(60);

  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#f5f5f5" }}>
      <Sidebar width={sidebarWidth} setWidth={setSidebarWidth} />
      <div
        style={{
          padding: "40px",
          flexGrow: 1,
          marginLeft: sidebarWidth,
          transition: "margin-left 0.3s ease",
          backgroundColor: "#f5f5f5",
          overflowY: "auto",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        }}
      >
        <h1 style={{ color: "#010528", marginBottom: "30px" }}>Available Quizzes</h1>

        <div style={{ 
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(350px, 1fr))",
          gap: "20px"
        }}>
          {quizzes.map((quiz, index) => (
            <QuizCard
              key={index}
              quiz={quiz.quiz}
              onClick={() => {
                setSelectedQuiz(quiz);
                setShowModal(true);
              }}
              onEdit={() => {
                setSelectedQuiz(quiz);
                // Здесь можно добавить логику для редактирования
              }}
            />
          ))}
        </div>

        {selectedQuiz && (
          <QuizModal
            visible={showModal}
            onClose={() => setShowModal(false)}
            quizConfig={selectedQuiz}
          />
        )}
      </div>
    </div>
  );
}
