function Sidebar({ width, setWidth }) {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const sidebarStyle = {
    height: "100vh",
    width: `${width}px`,
    backgroundColor: "rgba(1, 5, 40, 0.8)",
    color: "#fff",
    transition: "width 0.3s ease",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    position: "fixed",
    left: 0,
    top: 0,
    zIndex: 10,
    boxShadow: "4px 0 8px rgba(0,0,0,0.1)",
    overflow: "hidden",
  };

  return (
    <div
      style={sidebarStyle}
      onMouseEnter={() => setWidth(200)}
      onMouseLeave={() => setWidth(60)}
    >
      <div style={{ padding: "20px 10px" }}>
        <div 
          style={{ display: "flex", alignItems: "center", padding: "10px", cursor: "pointer" }}
          onClick={() => navigate("/")}
        >
          <FiHome size={24} style={{ marginRight: "12px" }} />
          {width > 100 && <span>Главная</span>}
        </div>
      </div>
      <div style={{ padding: "20px 10px" }}>
        <div 
          style={{ display: "flex", alignItems: "center", padding: "10px", cursor: "pointer" }}
          onClick={logout}
        >
          <FiLogOut size={24} style={{ marginRight: "12px" }} />
          {width > 100 && <span>Выйти</span>}
        </div>
      </div>
    </div>
  );
}
