import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useLocation, useNavigate } from "react-router-dom";
import { 
  FiLogOut, 
  FiUpload, 
  FiEye,
  FiHome,
  FiSettings,
  FiUser
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

function Sidebar() {
  const { logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [expanded, setExpanded] = useState(false);
  const [activeItem, setActiveItem] = useState('');

  useEffect(() => {
    // Определяем активный элемент на основе текущего пути
    const path = location.pathname;
    if (path.includes('upload')) setActiveItem('upload');
    else if (path.includes('quizzes')) setActiveItem('quizzes');
    else if (path === '/') setActiveItem('home');
  }, [location]);

  const sidebarStyle = {
    height: "100vh",
    width: expanded ? "250px" : "80px",
    backgroundColor: "#010528",
    color: "#fff",
    transition: "all 0.3s cubic-bezier(0.16, 1, 0.3, 1)",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    position: "fixed",
    left: 0,
    top: 0,
    zIndex: 100,
    boxShadow: "4px 0 15px rgba(0,0,0,0.2)",
    overflow: "hidden",
    borderRight: "1px solid rgba(255,255,255,0.1)"
  };

  const sectionStyle = {
    padding: "20px 15px",
  };

  const handleNavigation = (path) => {
    navigate(path);
    setActiveItem(path.replace('/', '') || 'home');
  };

  return (
    <div
      style={sidebarStyle}
      onMouseEnter={() => setExpanded(true)}
      onMouseLeave={() => setExpanded(false)}
    >
      <div style={sectionStyle}>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          marginBottom: '30px',
          padding: '0 10px'
        }}>
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            transition={{ type: 'spring', stiffness: 300 }}
          >
            <div style={{
              width: '40px',
              height: '40px',
              backgroundColor: '#0921E6',
              borderRadius: '10px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              marginRight: '15px'
            }}>
              <FiUser size={20} />
            </div>
          </motion.div>
          <AnimatePresence>
            {expanded && (
              <motion.h3
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.2 }}
                style={{ 
                  margin: 0,
                  fontSize: '18px',
                  fontWeight: '600',
                  whiteSpace: 'nowrap'
                }}
              >
                Quiz Creator
              </motion.h3>
            )}
          </AnimatePresence>
        </div>

        <SidebarItem 
          icon={<FiHome />} 
          label="Home" 
          active={activeItem === 'home'}
          onClick={() => handleNavigation('/')}
          expanded={expanded}
        />
        <SidebarItem 
          icon={<FiUpload />} 
          label="Upload" 
          active={activeItem === 'upload'}
          onClick={() => handleNavigation('/upload')}
          expanded={expanded}
        />
        <SidebarItem 
          icon={<FiEye />} 
          label="Quizzes" 
          active={activeItem === 'quizzes'}
          onClick={() => handleNavigation('/quizzes')}
          expanded={expanded}
        />
      </div>

      <div style={sectionStyle}>
        <SidebarItem 
          icon={<FiSettings />} 
          label="Settings" 
          onClick={() => {}}
          expanded={expanded}
        />
        <SidebarItem 
          icon={<FiLogOut />} 
          label="Logout" 
          onClick={logout}
          expanded={expanded}
        />
      </div>
    </div>
  );
}

function SidebarItem({ icon, label, onClick, active, expanded }) {
  const itemStyle = {
    display: "flex",
    alignItems: "center",
    padding: "12px 15px",
    borderRadius: "8px",
    cursor: "pointer",
    marginBottom: "8px",
    transition: "all 0.3s ease",
    color: "#fff",
    background: active ? "rgba(9, 33, 230, 0.2)" : "transparent",
    position: "relative",
    overflow: "hidden"
  };

  const iconStyle = {
    fontSize: "20px",
    marginRight: "15px",
    minWidth: "24px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: active ? "#fff" : "rgba(255,255,255,0.8)",
  };

  const activeIndicatorStyle = {
    position: 'absolute',
    left: 0,
    top: 0,
    bottom: 0,
    width: '4px',
    background: '#0921E6',
    borderRadius: '0 4px 4px 0',
    opacity: active ? 1 : 0,
    transition: 'opacity 0.3s ease'
  };

  return (
    <motion.div
      style={itemStyle}
      onClick={onClick}
      whileHover={{ 
        background: active ? "rgba(9, 33, 230, 0.3)" : "rgba(255,255,255,0.05)" 
      }}
      whileTap={{ scale: 0.98 }}
    >
      <div style={activeIndicatorStyle} />
      <div style={iconStyle}>{icon}</div>
      <AnimatePresence>
        {expanded && (
          <motion.span
            initial={{ opacity: 0, x: -10 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -10 }}
            transition={{ duration: 0.2 }}
            style={{
              whiteSpace: "nowrap",
              fontSize: '15px',
              color: active ? "#fff" : "rgba(255,255,255,0.8)",
              fontWeight: active ? '500' : '400'
            }}
          >
            {label}
          </motion.span>
        )}
      </AnimatePresence>
    </motion.div>
  );
}

export default Sidebar;
