import React, { useState, useEffect } from "react";
import yaml from "js-yaml";
import { FiLogOut, FiUpload, FiEye, FiX, FiCheck, FiPlus, FiTrash2, FiSave } from "react-icons/fi";
import { useAuth } from "../context/AuthContext";
import api from "../services/Api";

const YamlExample = () => {
  return (
    <div style={{ 
      backgroundColor: "#282c34",
      padding: "20px",
      borderRadius: "8px",
      overflowX: "auto",
      fontSize: "14px",
      lineHeight: "1.5",
      fontFamily: "'Fira Code', monospace, 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      <div style={{ color: "#abb2bf" }}>
        {/* YAML пример остаётся таким же */}
      </div>
    </div>
  );
};

const QuizModal = ({ visible, onClose, quizConfig }) => {
  if (!visible) return null;

  return (
    <div style={{
      position: "fixed",
      top: 0, left: 0, right: 0, bottom: 0,
      backgroundColor: "rgba(0, 0, 0, 0.5)",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      zIndex: 999,
    }}>
      <div style={{
        backgroundColor: "#fff",
        padding: "30px",
        borderRadius: "12px",
        width: "90%",
        maxWidth: "700px",
        maxHeight: "90vh",
        overflowY: "auto",
        position: "relative",
      }}>
        <button onClick={onClose} style={{ position: "absolute", top: "15px", right: "15px" }}>
          <FiX />
        </button>
        <Quiz quizConfig={quizConfig} />
      </div>
    </div>
  );
};

const Quiz = ({ quizConfig }) => {
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const handleChange = (questionId, optionIndex, isMultiple) => {
    setAnswers(prev => {
      const current = prev[questionId] || (isMultiple ? [] : null);
      if (isMultiple) {
        return { 
          ...prev, 
          [questionId]: current.includes(optionIndex) 
            ? current.filter(i => i !== optionIndex) 
            : [...current, optionIndex]
        };
      }
      return { ...prev, [questionId]: optionIndex };
    });
  };

  const checkAnswers = () => {
    let correct = 0;
    quizConfig.quiz.questions.forEach(q => {
      const a = answers[q.id];
      if (q.type === "single" && a === q.correct_option_index) correct++;
      if (q.type === "multiple" && Array.isArray(a) &&
          a.length === q.correct_option_indexes.length &&
          a.every(val => q.correct_option_indexes.includes(val))) {
        correct++;
      }
    });
    setResult(`Правильных ответов: ${correct} из ${quizConfig.quiz.questions.length}`);
  };

  return (
    <div>
      <h2>{quizConfig.quiz.title}</h2>
      <p>{quizConfig.quiz.description}</p>
      {quizConfig.quiz.questions.map(q => (
        <div key={q.id} style={{ marginBottom: 20 }}>
          <strong>{q.id}. {q.question}</strong>
          {q.options.map((opt, i) => (
            <label key={i} style={{ display: "flex", alignItems: "center", marginTop: 6 }}>
              <input
                type={q.type === "multiple" ? "checkbox" : "radio"}
                name={`q-${q.id}`}
                checked={q.type === "multiple" 
                  ? (answers[q.id] || []).includes(i) 
                  : answers[q.id] === i}
                onChange={() => handleChange(q.id, i, q.type === "multiple")}
                style={{ display: "none" }}
              />
              <CustomCheckbox 
                checked={q.type === "multiple" 
                  ? (answers[q.id] || []).includes(i) 
                  : answers[q.id] === i} 
                isRadio={q.type !== "multiple"}
              />
              <span style={{ marginLeft: 8 }}>{opt}</span>
            </label>
          ))}
        </div>
      ))}
      <button onClick={checkAnswers}>Проверить ответы</button>
      {result && <p style={{ fontWeight: "bold" }}>{result}</p>}
    </div>
  );
};

const CustomCheckbox = ({ checked, isRadio }) => (
  <span style={{
    width: 20,
    height: 20,
    borderRadius: isRadio ? "50%" : "4px",
    border: "2px solid #010528",
    backgroundColor: checked ? "#010528" : "transparent",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
  }}>
    {checked && (isRadio ? (
      <span style={{ width: 10, height: 10, borderRadius: "50%", backgroundColor: "#fff" }} />
    ) : (
      <FiCheck color="#fff" size={16} />
    ))}
  </span>
);

const ConfigUploadPage = () => {
  const [quizConfig, setQuizConfig] = useState(null);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sidebarWidth, setSidebarWidth] = useState(60);
  const [activeTab, setActiveTab] = useState('upload');
  const [savedQuizzes, setSavedQuizzes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const [quizTitle, setQuizTitle] = useState("");
  const [quizDescription, setQuizDescription] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    id: 1,
    text: "",
    type: "single",
    options: ["", ""],
    correctOption: null,
    correctOptions: []
  });

  useEffect(() => {
    const fetchQuizzes = async () => {
      setIsLoading(true);
      try {
        const response = await api.getQuizzes();
        setSavedQuizzes(response.data);
      } catch (err) {
        setError("Не удалось загрузить тесты");
      } finally {
        setIsLoading(false);
      }
    };
    fetchQuizzes();
  }, []);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const parsed = yaml.load(e.target.result);
        setQuizConfig(parsed);
        setShowModal(true);
      } catch (err) {
        setError("Ошибка парсинга YAML");
      }
    };
    reader.readAsText(file);
  };

  const saveQuiz = async () => {
    setIsLoading(true);
    setError(null);

    try {
      // Валидация
      if (!quizTitle.trim()) throw new Error("Введите название теста");
      if (questions.length === 0) throw new Error("Добавьте хотя бы один вопрос");

      // Подготовка данных для API
      const quizData = {
        title: quizTitle,
        description: quizDescription,
        course_id: 1,
        duration: 60,
        max_grade: 100,
        start_date: new Date().toISOString(),
        end_date: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
        state_id: 1
      };

      // Создаем квиз
      const quizResponse = await api.createQuiz(quizData);
      const quizId = quizResponse.data.id;

      // Добавляем вопросы
      for (const question of questions) {
        const questionData = {
          question_text: question.question,
          question_type_id: question.type === 'single' ? 1 : 2,
          quiz_id: quizId
        };

        const questionResponse = await api.createQuestion(quizId, questionData);
        const questionId = questionResponse.data.id;

        // Добавляем варианты ответов
        const optionIds = [];
        for (const optionText of question.options) {
          const optionResponse = await api.createOption(questionId, {
            option_text: optionText,
            question_id: questionId
          });
          optionIds.push(optionResponse.data.id);
        }

        // Добавляем правильные ответы
        if (question.type === 'single') {
          await api.addCorrectAnswer(questionId, {
            option_id: optionIds[question.correct_option_index]
          });
        } else {
          for (const correctIndex of question.correct_option_indexes) {
            await api.addCorrectAnswer(questionId, {
              option_id: optionIds[correctIndex]
            });
          }
        }
      }

      // Обновляем список
      const updatedResponse = await api.getQuizzes();
      setSavedQuizzes(updatedResponse.data);
      alert('Квиз успешно сохранен!');
    } catch (err) {
      setError(err.message || "Ошибка сохранения");
    } finally {
      setIsLoading(false);
    }
  };

  const addQuestion = () => {
    if (!currentQuestion.text.trim()) {
      setError("Введите текст вопроса");
      return;
    }

    if (currentQuestion.options.every(opt => !opt.trim())) {
      setError("Добавьте хотя бы один вариант");
      return;
    }

    if (currentQuestion.type === "single" && currentQuestion.correctOption === null) {
      setError("Выберите правильный вариант");
      return;
    }

    if (currentQuestion.type === "multiple" && currentQuestion.correctOptions.length === 0) {
      setError("Выберите хотя бы один правильный вариант");
      return;
    }

    const newQuestion = {
      id: questions.length + 1,
      question: currentQuestion.text,
      type: currentQuestion.type,
      options: currentQuestion.options.filter(opt => opt.trim() !== ""),
      correct_option_index: currentQuestion.correctOption,
      correct_option_indexes: currentQuestion.correctOptions
    };

    setQuestions([...questions, newQuestion]);
    setCurrentQuestion({
      id: questions.length + 2,
      text: "",
      type: "single",
      options: ["", ""],
      correctOption: null,
      correctOptions: []
    });
    setError(null);
  };

  const updateOption = (index, value) => {
    const newOptions = [...currentQuestion.options];
    newOptions[index] = value;
    setCurrentQuestion({...currentQuestion, options: newOptions});
  };

  const addOption = () => {
    if (currentQuestion.options.length >= 10) {
      setError("Максимум 10 вариантов");
      return;
    }
    setCurrentQuestion({
      ...currentQuestion,
      options: [...currentQuestion.options, ""]
    });
  };

  const removeOption = (index) => {
    const newOptions = currentQuestion.options.filter((_, i) => i !== index);
    setCurrentQuestion({
      ...currentQuestion,
      options: newOptions,
      correctOption: currentQuestion.correctOption === index ? null : 
                   currentQuestion.correctOption > index ? currentQuestion.correctOption - 1 : 
                   currentQuestion.correctOption,
      correctOptions: currentQuestion.correctOptions
        .map(opt => opt > index ? opt - 1 : opt)
        .filter(opt => opt !== index)
    });
  };

  const handleCorrectAnswerChange = (index, isChecked) => {
    if (currentQuestion.type === "single") {
      setCurrentQuestion({...currentQuestion, correctOption: index});
    } else {
      const newCorrect = isChecked 
        ? [...currentQuestion.correctOptions, index]
        : currentQuestion.correctOptions.filter(i => i !== index);
      setCurrentQuestion({...currentQuestion, correctOptions: newCorrect});
    }
  };

  const generateYAML = () => {
    const quiz = {
      quiz: {
        title: quizTitle,
        description: quizDescription,
        questions: questions
      }
    };

    const yamlStr = yaml.dump(quiz);
    const blob = new Blob([yamlStr], { type: "text/yaml" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = `quiz_${Date.now()}.yaml`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const previewQuiz = () => {
    setQuizConfig({
      quiz: {
        title: quizTitle,
        description: quizDescription,
        questions: questions
      }
    });
    setShowModal(true);
  };

  const loadSavedQuiz = (quiz) => {
    setQuizTitle(quiz.title);
    setQuizDescription(quiz.description);
    setQuestions(quiz.questions || []);
    setActiveTab('create');
  };

  const deleteSavedQuiz = async (id, e) => {
    e.stopPropagation();
    try {
      await api.deleteQuiz(id);
      setSavedQuizzes(savedQuizzes.filter(q => q.id !== id));
    } catch (err) {
      setError("Не удалось удалить тест");
    }
  };

  const renderGUICreator = () => (
    <div style={{ backgroundColor: "#fff", padding: "30px", borderRadius: "12px" }}>
      <h2>Create new quiz</h2>

      <div style={{ marginBottom: "25px" }}>
        <label>Quiz Title:</label>
        <input
          type="text"
          value={quizTitle}
          onChange={(e) => setQuizTitle(e.target.value)}
          placeholder="Enter quiz title"
        />
      </div>

      <div style={{ marginBottom: "25px" }}>
        <label>Description:</label>
        <textarea
          value={quizDescription}
          onChange={(e) => setQuizDescription(e.target.value)}
          placeholder="Enter quiz description"
        />
      </div>

      <div style={{ backgroundColor: "#f8f9fa", padding: "20px", borderRadius: "8px" }}>
        <h3>Add Question</h3>

        <div style={{ marginBottom: "15px" }}>
          <label>Question Text:</label>
          <input
            type="text"
            value={currentQuestion.text}
            onChange={(e) => setCurrentQuestion({...currentQuestion, text: e.target.value})}
            placeholder="Enter your question"
          />
        </div>

        <div style={{ marginBottom: "15px" }}>
          <label>Question Type:</label>
          <select
            value={currentQuestion.type}
            onChange={(e) => setCurrentQuestion({
              ...currentQuestion, 
              type: e.target.value,
              correctOption: null,
              correctOptions: []
            })}
          >
            <option value="single">Single correct answer</option>
            <option value="multiple">Multiple correct answers</option>
          </select>
        </div>

        <div style={{ marginBottom: "20px" }}>
          <label>Options:</label>
          {currentQuestion.options.map((option, index) => (
            <div key={index} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
              <input
                type={currentQuestion.type === "single" ? "radio" : "checkbox"}
                name={`correct-answer-${currentQuestion.id}`}
                checked={
                  currentQuestion.type === "single" 
                    ? currentQuestion.correctOption === index 
                    : currentQuestion.correctOptions.includes(index)
                }
                onChange={(e) => handleCorrectAnswerChange(index, e.target.checked)}
              />
              <input
                type="text"
                value={option}
                onChange={(e) => updateOption(index, e.target.value)}
                placeholder={`Option ${index + 1}`}
              />
              <button onClick={() => removeOption(index)}>
                <FiTrash2 />
              </button>
            </div>
          ))}
          <button onClick={addOption} disabled={currentQuestion.options.length >= 10}>
            <FiPlus /> Add Option
          </button>
        </div>

        <button
          onClick={addQuestion}
          disabled={
            !currentQuestion.text || 
            currentQuestion.options.every(opt => !opt.trim()) ||
            (currentQuestion.type === "single" && currentQuestion.correctOption === null) ||
            (currentQuestion.type === "multiple" && currentQuestion.correctOptions.length === 0)
          }
        >
          Add Question
        </button>
      </div>

      {questions.length > 0 && (
        <div>
          <h3>Added Questions ({questions.length})</h3>
          <div style={{ backgroundColor: "#f8f9fa", padding: "15px", borderRadius: "8px" }}>
            {questions.map((q, i) => (
              <div key={i} style={{ padding: "12px", borderBottom: i < questions.length - 1 ? "1px solid #ddd" : "none" }}>
                <div>
                  <div>{q.id}. {q.question}</div>
                  <div>Type: {q.type === "single" ? "Single answer" : "Multiple answers"}</div>
                </div>
                <button onClick={() => setQuestions(questions.filter((_, idx) => idx !== i))}>
                  <FiTrash2 />
                </button>
              </div>
            ))}
          </div>

          <div style={{ display: "flex", gap: "15px", marginTop: "20px" }}>
            <button onClick={previewQuiz}>
              <FiEye /> Preview Quiz
            </button>
            <button onClick={generateYAML} disabled={!quizTitle || questions.length === 0}>
              <FiUpload /> Export as YAML
            </button>
            <button 
              onClick={saveQuiz} 
              disabled={!quizTitle || questions.length === 0 || isLoading}
            >
              {isLoading ? "Saving..." : <><FiSave /> Save Quiz</>}
            </button>
          </div>
        </div>
      )}
    </div>
  );

  const renderSavedQuizzes = () => (
    <div style={{ backgroundColor: "#fff", padding: "30px", borderRadius: "12px" }}>
      <h2>Saved Quizzes</h2>

      {isLoading ? (
        <p>Loading...</p>
      ) : savedQuizzes.length === 0 ? (
        <p>No saved quizzes yet</p>
      ) : (
        <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
          {savedQuizzes.map((quiz) => (
            <div 
              key={quiz.id}
              onClick={() => loadSavedQuiz(quiz)}
              style={{ padding: "15px", border: "1px solid #ddd", borderRadius: "8px", cursor: "pointer" }}
            >
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                  <h3>{quiz.title}</h3>
                  <p>{quiz.description}</p>
                </div>
                <button onClick={(e) => deleteSavedQuiz(quiz.id, e)}>
                  <FiTrash2 />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  const renderUploadTab = () => (
    <div style={{ backgroundColor: "#fff", padding: "30px", borderRadius: "12px" }}>
      <h2>Upload Quiz Config</h2>
      <label htmlFor="config-upload">
        <FiUpload /> Select YAML File
        <input
          type="file"
          id="config-upload"
          style={{ display: "none" }}
          accept=".yaml,.yml"
          onChange={handleFileUpload}
        />
      </label>

      {error && (
        <div style={{ backgroundColor: "#f8d7da", color: "#721c24", padding: "15px", borderRadius: "6px" }}>
          {error}
        </div>
      )}

      <div style={{ marginTop: "30px" }}>
        <h3>Example YAML Structure</h3>
        <YamlExample />
      </div>
    </div>
  );

  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#f5f5f5" }}>
      <div
        style={{
          height: "100vh",
          width: `${sidebarWidth}px`,
          backgroundColor: "rgba(1, 5, 40, 0.8)",
          transition: "width 0.3s ease",
          position: "fixed",
          left: 0,
          top: 0,
        }}
        onMouseEnter={() => setSidebarWidth(200)}
        onMouseLeave={() => setSidebarWidth(60)}
      >
        <div style={{ padding: "20px 10px" }}>
          <div onClick={() => setActiveTab('upload')}>
            <FiUpload /> {sidebarWidth > 60 && "Upload Config"}
          </div>
          <div onClick={() => setActiveTab('create')}>
            <FiPlus /> {sidebarWidth > 60 && "Create New"}
          </div>
          <div onClick={() => setActiveTab('saved')}>
            <FiSave /> {sidebarWidth > 60 && "Saved Quizzes"}
          </div>
        </div>
      </div>

      <div style={{ padding: "40px", flexGrow: 1, marginLeft: `${sidebarWidth}px` }}>
        <h1>Quiz Creator</h1>

        {error && (
          <div style={{ backgroundColor: "#ffecec", color: "#ff0000", padding: "10px", marginBottom: "20px" }}>
            {error}
          </div>
        )}

        {activeTab === 'upload' ? renderUploadTab() : 
         activeTab === 'create' ? renderGUICreator() : 
         renderSavedQuizzes()}

        <QuizModal
          visible={showModal}
          onClose={() => setShowModal(false)}
          quizConfig={quizConfig}
        />
      </div>
    </div>
  );
};

export default ConfigUploadPage;
