// Компонент квиза
function Quiz({ quizConfig }) {
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const handleAnswerChange = (questionId, optionIndex, isMultiple) => {
    setAnswers(prev => {
      const current = prev[questionId] || (isMultiple ? [] : null);

      if (isMultiple) {
        return {
          ...prev,
          [questionId]: current.includes(optionIndex) 
            ? current.filter(i => i !== optionIndex) 
            : [...current, optionIndex]
        };
      } else {
        return { ...prev, [questionId]: optionIndex };
      }
    });
  };

  const checkAnswers = () => {
    let correct = 0;
    quizConfig.quiz.questions.forEach(q => {
      const a = answers[q.id];
      if (q.type === "single" && a === q.correct_option_index) correct++;
      if (q.type === "multiple" && Array.isArray(a) && 
          a.length === q.correct_option_indexes.length && 
          a.every(val => q.correct_option_indexes.includes(val))) {
        correct++;
      }
    });
    setResult(`Правильных ответов: ${correct} из ${quizConfig.quiz.questions.length}`);
  };

  return (
    <div>
      <h2 style={{ color: "#010528" }}>{quizConfig.quiz.title}</h2>
      <p>{quizConfig.quiz.description}</p>

      {quizConfig.quiz.questions.map(q => (
        <div key={q.id} style={{ marginBottom: "20px" }}>
          <strong style={{ color: "#010528" }}>{q.id}. {q.question}</strong>
          {q.options.map((opt, i) => (
            <label key={i} style={{ display: "flex", alignItems: "center", margin: "6px 0", cursor: "pointer" }}>
              <input
                type={q.type === "multiple" ? "checkbox" : "radio"}
                name={`q-${q.id}`}
                checked={q.type === "multiple" 
                  ? (answers[q.id] || []).includes(i) 
                  : answers[q.id] === i}
                onChange={() => handleAnswerChange(q.id, i, q.type === "multiple")}
                style={{ marginRight: "8px" }}
              />
              {opt}
            </label>
          ))}
        </div>
      ))}

      <Button primary onClick={checkAnswers}>Проверить ответы</Button>
      {result && <p style={{ marginTop: "10px", fontWeight: "bold" }}>{result}</p>}
    </div>
  );
}

// Главный компонент страницы
function ConfigUploadPage() {
  const [quizConfig, setQuizConfig] = useState(null);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('upload');
  const [savedQuizzes, setSavedQuizzes] = useState([]);
  const [quizTitle, setQuizTitle] = useState("");
  const [quizDescription, setQuizDescription] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    id: 1,
    text: "",
    type: "single",
    options: ["", ""],
    correctOption: null,
    correctOptions: []
  });

  const { logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem('savedQuizzes');
    if (saved) setSavedQuizzes(JSON.parse(saved));
  }, []);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        setQuizConfig(yaml.load(e.target.result));
        setError(null);
        setShowModal(true);
      } catch (err) {
        setError("Ошибка парсинга YAML: " + err.message);
      }
    };
    reader.readAsText(file);
  };

  const saveQuiz = () => {
    const newQuiz = {
      id: Date.now(),
      title: quizTitle,
      description: quizDescription,
      questions,
      createdAt: new Date().toISOString()
    };

    const updated = [...savedQuizzes, newQuiz];
    setSavedQuizzes(updated);
    localStorage.setItem('savedQuizzes', JSON.stringify(updated));
    alert('Квиз сохранен!');
  };

  const loadSavedQuiz = (quiz) => {
    setQuizTitle(quiz.title);
    setQuizDescription(quiz.description);
    setQuestions(quiz.questions);
    setActiveTab('create');
  };

  const deleteSavedQuiz = (id, e) => {
    e.stopPropagation();
    const updated = savedQuizzes.filter(q => q.id !== id);
    setSavedQuizzes(updated);
    localStorage.setItem('savedQuizzes', JSON.stringify(updated));
  };

  const addQuestion = () => {
    const newQuestion = {
      id: questions.length + 1,
      question: currentQuestion.text,
      type: currentQuestion.type,
      options: currentQuestion.options.filter(opt => opt.trim() !== ""),
      ...(currentQuestion.type === "single" 
        ? { correct_option_index: currentQuestion.correctOption } 
        : { correct_option_indexes: currentQuestion.correctOptions })
    };

    setQuestions([...questions, newQuestion]);
    setCurrentQuestion({
      id: questions.length + 2,
      text: "",
      type: "single",
      options: ["", ""],
      correctOption: null,
      correctOptions: []
    });
  };

  const updateOption = (index, value) => {
    const newOptions = [...currentQuestion.options];
    newOptions[index] = value;
    setCurrentQuestion({...currentQuestion, options: newOptions});
  };

  const addOption = () => {
    setCurrentQuestion({
      ...currentQuestion,
      options: [...currentQuestion.options, ""]
    });
  };

  const removeOption = (index) => {
    const newOptions = currentQuestion.options.filter((_, i) => i !== index);
    setCurrentQuestion({
      ...currentQuestion,
      options: newOptions,
      correctOption: currentQuestion.correctOption === index ? null : 
                   currentQuestion.correctOption > index ? currentQuestion.correctOption - 1 : 
                   currentQuestion.correctOption,
      correctOptions: currentQuestion.correctOptions
        .map(opt => opt > index ? opt - 1 : opt)
        .filter(opt => opt !== index)
    });
  };

  const handleCorrectAnswerChange = (index, isChecked) => {
    if (currentQuestion.type === "single") {
      setCurrentQuestion({...currentQuestion, correctOption: index});
    } else {
      const newCorrect = isChecked 
        ? [...currentQuestion.correctOptions, index]
        : currentQuestion.correctOptions.filter(i => i !== index);
      setCurrentQuestion({...currentQuestion, correctOptions: newCorrect});
    }
  };

  const generateYAML = () => {
    const quiz = {
      quiz: {
        title: quizTitle,
        description: quizDescription,
        questions: questions
      }
    };

    const yamlStr = yaml.dump(quiz);
    const blob = new Blob([yamlStr], { type: "text/yaml" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = `quiz_${Date.now()}.yaml`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const previewQuiz = () => {
    setQuizConfig({
      quiz: {
        title: quizTitle,
        description: quizDescription,
        questions: questions
      }
    });
    setShowModal(true);
  };

  const renderGUICreator = () => (
    <Card>
      <h2 style={{ color: "#010528", marginBottom: "20px" }}>Создание нового квиза</h2>

      <div style={{ marginBottom: "25px" }}>
        <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>Название квиза:</label>
        <input
          type="text"
          value={quizTitle}
          onChange={(e) => setQuizTitle(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ddd",
            fontSize: "16px"
          }}
          placeholder="Введите название квиза"
        />
      </div>

      <div style={{ marginBottom: "25px" }}>
        <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>Описание:</label>
        <textarea
          value={quizDescription}
          onChange={(e) => setQuizDescription(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ddd",
            minHeight: "80px",
            fontSize: "16px"
          }}
          placeholder="Введите описание квиза"
        />
      </div>

      <div style={{ 
        backgroundColor: "#f8f9fa",
        padding: "20px",
        borderRadius: "8px",
        marginBottom: "25px"
      }}>
        <h3 style={{ color: "#010528", marginTop: 0, marginBottom: "15px" }}>Добавить вопрос</h3>

        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>Текст вопроса:</label>
          <input
            type="text"
            value={currentQuestion.text}
            onChange={(e) => setCurrentQuestion({...currentQuestion, text: e.target.value})}
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "6px",
              border: "1px solid #ddd",
              fontSize: "16px"
            }}
            placeholder="Введите ваш вопрос"
          />
        </div>

        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>Тип вопроса:</label>
          <select
            value={currentQuestion.type}
            onChange={(e) => setCurrentQuestion({
              ...currentQuestion, 
              type: e.target.value,
              correctOption: null,
              correctOptions: []
            })}
            style={{
              padding: "10px",
              borderRadius: "6px",
              border: "1px solid #ddd",
              width: "100%",
              fontSize: "16px"
            }}
          >
            <option value="single">Один правильный ответ</option>
            <option value="multiple">Несколько правильных ответов</option>
          </select>
        </div>

        <div style={{ marginBottom: "20px" }}>
          <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>Варианты ответов:</label>
          {currentQuestion.options.map((option, index) => (
            <div key={index} style={{ 
              display: "flex", 
              alignItems: "center", 
              marginBottom: "10px",
              gap: "10px"
            }}>
              <input
                type={currentQuestion.type === "single" ? "radio" : "checkbox"}
                name={`correct-answer-${currentQuestion.id}`}
                checked={
                  currentQuestion.type === "single" 
                    ? currentQuestion.correctOption === index 
                    : currentQuestion.correctOptions.includes(index)
                }
                onChange={(e) => handleCorrectAnswerChange(index, e.target.checked)}
                style={{ transform: "scale(1.2)" }}
              />
              <input
                type="text"
                value={option}
                onChange={(e) => updateOption(index, e.target.value)}
                style={{
                  flex: 1,
                  padding: "8px 10px",
                  borderRadius: "6px",
                  border: "1px solid #ddd",
                  fontSize: "16px"
                }}
                placeholder={`Вариант ${index + 1}`}
              />
              <button 
                onClick={() => removeOption(index)}
                style={{
                  background: "none",
                  border: "none",
                  cursor: "pointer",
                  color: "#ff4444",
                  fontSize: "18px",
                  display: "flex",
                  alignItems: "center"
                }}
              >
                <FiTrash2 />
              </button>
            </div>
          ))}
          <button
            onClick={addOption}
            style={{
              padding: "8px 15px",
              backgroundColor: "#f0f0f0",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              gap: "8px",
              fontSize: "15px",
              marginTop: "10px"
            }}
          >
            <FiPlus size={16} /> Добавить вариант
          </button>
        </div>

        <Button
          primary
          onClick={addQuestion}
          disabled={!currentQuestion.text || 
                   currentQuestion.options.every(opt => !opt.trim()) ||
                   (currentQuestion.type === "single" && currentQuestion.correctOption === null) ||
                   (currentQuestion.type === "multiple" && currentQuestion.correctOptions.length === 0)}
        >
          Добавить вопрос
        </Button>
      </div>

      {questions.length > 0 && (
        <div>
          <h3 style={{ color: "#010528", marginBottom: "15px" }}>Добавленные вопросы ({questions.length})</h3>
          <div style={{ 
            backgroundColor: "#f8f9fa", 
            padding: "15px", 
            borderRadius: "8px",
            marginBottom: "25px"
          }}>
            {questions.map((q, i) => (
              <div key={i} style={{ 
                padding: "12px", 
                borderBottom: i < questions.length - 1 ? "1px solid #ddd" : "none",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center"
              }}>
                <div>
                  <div style={{ fontWeight: "500", marginBottom: "5px" }}>{q.id}. {q.question}</div>
                  <div style={{ fontSize: "14px", color: "#666" }}>
                    Тип: {q.type === "single" ? "Один ответ" : "Несколько ответов"}
                  </div>
                </div>
                <button 
                  onClick={() => setQuestions(questions.filter((_, idx) => idx !== i))}
                  style={{
                    background: "none",
                    border: "none",
                    cursor: "pointer",
                    color: "#ff4444",
                    fontSize: "18px"
                  }}
                >
                  <FiTrash2 />
                </button>
              </div>
            ))}
          </div>

          <div style={{ display: "flex", gap: "15px", flexWrap: 'wrap' }}>
            <Button
              primary
              onClick={previewQuiz}
              style={{ backgroundColor: "#0921E6" }}
            >
              <FiEye /> Предпросмотр
            </Button>
            <Button
              primary
              onClick={generateYAML}
              disabled={!quizTitle || questions.length === 0}
              style={{ backgroundColor: "#28a745" }}
            >
              <FiUpload /> Экспорт в YAML
            </Button>
            <Button
              primary
              onClick={saveQuiz}
              disabled={!quizTitle || questions.length === 0}
              style={{ backgroundColor: "#6f42c1" }}
            >
              <FiSave /> Сохранить квиз
            </Button>
          </div>
        </div>
      )}
    </Card>
  );

  const renderSavedQuizzes = () => (
    <Card>
      <h2 style={{ color: "#010528", marginBottom: "20px" }}>Сохраненные квизы</h2>

      {savedQuizzes.length === 0 ? (
        <p style={{ color: "#666", textAlign: "center" }}>Нет сохраненных квизов</p>
      ) : (
        <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
          {savedQuizzes.map((quiz) => (
            <div 
              key={quiz.id}
              onClick={() => loadSavedQuiz(quiz)}
              style={{
                padding: "15px",
                border: "1px solid #ddd",
                borderRadius: "8px",
                cursor: "pointer",
                transition: "background-color 0.3s",
                ':hover': {
                  backgroundColor: "#f8f9fa"
                }
              }}
            >
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                  <h3 style={{ margin: 0, color: "#010528" }}>{quiz.title}</h3>
                  <p style={{ margin: "5px 0 0", color: "#666" }}>{quiz.description}</p>
                  <p style={{ margin: "5px 0 0", fontSize: "14px", color: "#999" }}>
                    {new Date(quiz.createdAt).toLocaleString()}
                  </p>
                </div>
                <button 
                  onClick={(e) => deleteSavedQuiz(quiz.id, e)}
                  style={{
                    background: "none",
                    border: "none",
                    cursor: "pointer",
                    color: "#ff4444",
                    fontSize: "18px",
                    display: "flex",
                    alignItems: "center"
                  }}
                >
                  <FiTrash2 />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </Card>
  );

  const renderUploadTab = () => (
    <Card>
      <h2 style={{ color: "#010528", marginBottom: "20px" }}>Загрузка конфигурации квиза</h2>
      <label>
        <Button primary as="div" style={{ display: "inline-flex" }}>
          <FiUpload /> Выбрать YAML файл
          <input
            type="file"
            style={{ display: "none" }}
            accept=".yaml,.yml"
            onChange={handleFileUpload}
          />
        </Button>
      </label>

      {error && (
        <div style={{ 
          backgroundColor: "#f8d7da",
          color: "#721c24",
          padding: "15px",
          borderRadius: "6px",
          marginBottom: "20px"
        }}>
          {error}
        </div>
      )}

      <div style={{ marginTop: "30px" }}>
        <h3 style={{ color: "#010528", marginBottom: "15px" }}>Пример структуры YAML</h3>
        <YamlExample />
      </div>
    </Card>
  );

  return (
    <PageContainer>
      <Sidebar 
        activeTab={activeTab} 
        setActiveTab={setActiveTab} 
        savedQuizzesCount={savedQuizzes.length} 
      />

      <MainContent>
        <h1 style={{ color: "#010528", marginBottom: "30px" }}>Конструктор квизов</h1>

        <div style={{ 
          display: "flex", 
          gap: "20px",
          marginBottom: "30px"
        }}>
          <Button
            primary={activeTab === 'upload'}
            onClick={() => setActiveTab('upload')}
          >
            <FiUpload /> Загрузка
          </Button>
          <Button
            primary={activeTab === 'create'}
            onClick={() => setActiveTab('create')}
          >
            <FiPlus /> Создать
          </Button>
          <Button
            primary={activeTab === 'saved'}
            onClick={() => setActiveTab('saved')}
          >
            <FiSave /> Сохраненные ({savedQuizzes.length})
          </Button>
        </div>

        {activeTab === 'upload' && renderUploadTab()}
        {activeTab === 'create' && renderGUICreator()}
        {activeTab === 'saved' && renderSavedQuizzes()}

        <QuizModal
          visible={showModal}
          onClose={() => setShowModal(false)}
          quizConfig={quizConfig}
        />
      </MainContent>
    </PageContainer>
  );
}

export default ConfigUploadPage;
